import React from 'react';
import { withState } from 'react-envelope';
import makePromiseState from './makePromiseState';

const enhance = withState({
  promise: makePromiseState(),
});

function Component({ changeUsername }, { promise }) {
  return (
    <div>
      {promise.loading && <p>Please wait...</p>}
      {promise.error && <p>Error: {promise.error}</p>}
      <p>
        <button onClick={() => promise.execute(changeUsername('John'))}>
          Change username to John
        </button>
      </p>
    </div>
  );
}

export default enhance(Component);
