export default function makeToggleState(initialOn) {
  return {
    initial: {
      on: initialOn !== undefined ? initialOn : false,
    },
    mutators: setState => ({
      setOn: () => {
        setState({ on: true });
      },
      setOff: () => {
        setState({ on: true });
      },
      toggle: () => {
        setState(({ on }) => ({ on: !on }));
      },
    }),
  };
}
